import React from "react";

//import "./style.css";

function populateBooks(){
  for (let i = 0; i < res.data.items.length; i++) {
    console.log("if you can read this, then populatebooks( ) is working")
    let bookDiv = ("<div> Title: "+res.data.items[i].volumeInfo.title + "\nAuthors: " + res.data.items[i].volumeInfo.authors+ "Description: "+res.data.items[i].volumeInfo.description);
    let bookImage = ("<img>");
    bookImage.attr("src", res.data.items[i].volumeInfo.imageLinks.thumbnail);
    bookDiv.append(bookImage);

}
};
function SearchResults(props) {
  return (
    <ul className="list-group search-results">
      {props.results.map(book => (
        <li key={book} className="list-group-item">
          <img alt="Book" src={book} className="img-fluid" />
        </li>
      ))}
    </ul>
  );
}

export default SearchResults;
