        var bookDiv = ("<div>");

        var bookImage = ("<img>");
        bookImage.attr("src", res.data.items[i].volumeInfo.imageLinks.thumbnail);

        bookDiv.prepend(bookImage);

        ("#books").append(bookImage);

console.log("if you can read this, then populatebooks( ) is working")
    }
  };
*/
  handleSearchClick = () => {
    API.getBooks()
      .then(res => this.setState({ books: res.data }))
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    this.setState({ search: event.target.value });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    API.getBooks(this.state.search)
      .then(res => {




        if (res.data.status === "error") {
          throw new Error(res.data.message);
        }
        this.setState({ results: res.data.message, error: "" }
        );


        console.log("Title: " + res.data.items[0].volumeInfo.title, + "Authors: " + res.data.items[0].volumeInfo.authors, res.data.items[0].volumeInfo.description, res.data.items[0].volumeInfo.imageLinks.thumbnail);

      })
      .catch(err => this.setState({ error: err.message }));

  };
  render() {
    return (
      <div>


        <Jumbotron></Jumbotron>

          <SearchForm
            handleFormSubmit={this.handleFormSubmit}
            handleSearchClick={this.handleSearchClick}
            handleInputChange={this.handleInputChange}
            books={this.state.books}
          />
          <table>
        {this.populateBooks()}
      </table>
      <SearchResults> </SearchResults>
      </div>
    );
  }
}

export default Search;
